pragma solidity 0.8.25;

import { CorrelatedTokenOracle } from "./common/CorrelatedTokenOracle.sol";
import { IwOS } from "../Interfaces/IwOS.sol";
import { ensureNonzeroAddress } from "../lib/validators.sol";

/**
 * @title WOSOracle
 * @author Enclabs
 * @notice This oracle fetches the price of wOS. Using 1:1 OS/S
 */
contract WOSOracle is CorrelatedTokenOracle {
    /// @notice Address of wOS contract
    /// @custom:oz-upgrades-unsafe-allow state-variable-immutable
    IwOS public immutable wOS;

    /// @notice Constructor for the implementation contract.
    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor(
        address _wOS,
        address _wS,
        address _resilientOracle
    ) CorrelatedTokenOracle(_wOS, _wS, _resilientOracle) {
        ensureNonzeroAddress(_wOS);
        ensureNonzeroAddress(_wS);
        wOS = IwOS(_wOS);
    }

    /**
     * @notice Gets the OS for 1 wOS
     * @return amount Amount of eETH
     */
    function _getUnderlyingAmount() internal view override returns (uint256) {
        return wOS.convertToAssets(1e18);
    }
}
